function showBreadcrumb() {
    var t = $("#PageContentLeftMenuTag .open a:eq(0)"), n;
    $(".breadcrumb").append('<li><a href="' + t.attr("href") + '">' + t.text() + "<\/a><\/li>");
    n = $("#PageContentLeftMenuTag .open .on");
    n.length && $(".breadcrumb").append('<li><a href="' + n.attr("href") + '">' + n.text() + "<\/a><\/li>")
}

